import React, { useState } from 'react'
import {motion} from "framer-motion";
import PropTypes from 'prop-types'
import './create.css'
import AnimatedText from "./AnimatedText";




const Create = (props) => {
    const handleReplay = () => {
        setReplay(!replay);
        setTimeout(() => {
            setReplay(true);
        }, 600);
    };

    const [replay, setReplay] = useState(true);

    return (<motion.section
            className="App"
            initial="hidden"
            // animate="visible"
            animate={replay ? "visible" : "hidden"}
        >
            <section>
                <h2 className={`section-index`}>01</h2>
                <div className={`text-container`}>
                    {/*<h1><AnimatedText type={'heading1'} text={'create'}/></h1>*/}
                    <div>
                        <h3>MULTIPLE CREATIVE TOOLS</h3>
                        <p>From wearables, emotes, scenes, or even names, there is an infinity of things to create and
                            imagine!
                            Explore all the creative tools in decentraland.</p>
                        <button onClick={handleReplay}>
                            Replay <span>‚ü≤</span>
                        </button>
                        <button className={`btn_inverted`}>START DEVELOPING</button>
                    </div>
                </div>
                <div className={`img-container`}>
                    <img
                        alt={props.img_alt}
                        src={props.img_src}
                        className="create-img"
                    />
                </div>

            </section>

        </motion.section>


    )
}

Create.defaultProps = {
    img_src: '/playground_assets/create.png',
    img_alt: 'image',
    rootClassName: '',
}

Create.propTypes = {
    img_src: PropTypes.string,
    img_alt: PropTypes.string,
    rootClassName: PropTypes.string,
}

export default Create
