import React, {useRef, useState} from 'react'
import PropTypes from 'prop-types'
import './missions.css'
import {useInView} from "framer-motion"
import AnimatedCharacters from "./AnimatedCharacters";
import AnimatedWords from "./AnimatedWords";
import {
    motion,
} from "framer-motion";

const letterAnimation = {
    visible: {
        transition: {
            staggerChildren: 0.03
        }
    }
};
const pAnimation = {
    hidden: {
        y: 15,
        opacity: 0,
        transition: {ease: [0.455, 0.03, 0.515, 0.955], duration: 0.85}
    },
    visible: {
        y: 0,
        opacity: 1,
        transition: {staggerChildren: 0.05, ease: [0.455, 0.03, 0.515, 0.955], duration: 0.85},
    }
};

// function Section({children}) {
//     const ref = useRef(null);
//     const isInView = useInView(ref, {amount: 0.5, once: true});
//
//     return (
//         <motion.section
//             ref={ref}
//             initial="hidden"
//             variants={letterAnimation}
//             animate={isInView ? "visible" : "hidden"}
//         >
//             {children}
//         </motion.section>
//     )
// }

export default function Worlds(props) {
    const ref = useRef(null);
    const isInView = useInView(ref, {amount: 0.5, once: true});

    return (
        <>
            <div className={`worlds__container`} ref={ref}>
                <motion.div ref={ref}
                            initial="hidden"
                            variants={letterAnimation}
                            animate={isInView ? "visible" : "hidden"}>
                    <AnimatedCharacters type={'heading1'} text={'WORLDS'}></AnimatedCharacters>
                    <AnimatedWords type={'heading3'} text={'YOUR PERSONAL SPACE IN THE METAVERSE'} y={'200%'}/>
                    <p>With just a Decentraland NAME you can claim your own corner of the metaverse, separate from the
                        open
                        world of Decentraland, where you can build, experiment, host events—whatever you want, it’s your
                        World! </p>
                    <button>START DEVELOPING</button>
                    <button>START DEVELOPING</button>
                </motion.div>
                <motion.div>
                    <img src={props.img_src} alt={props.img_alt}/>
                </motion.div>
            </div>
        </>
    );
}


Worlds.defaultProps = {
    img_src: '/playground_assets/worlds.png',
    img_alt: 'image',
    rootClassName: '',
}

Worlds.propTypes = {
    img_src: PropTypes.string,
    img_alt: PropTypes.string,
    rootClassName: PropTypes.string,
}
