                    </div>
                </div>
                <div className={`img-container`} >
                    <img
                        alt={props.img_alt}
                        src={props.img_src}
                        className="create-img"
                    />
                </div>

            </section>
        <button onClick={handleReplay}>
            Replay <span>‚ü≤</span>
        </button>
    </motion.section>


    )
}

Create.defaultProps = {
    img_src: '/playground_assets/create.png',
