import React, {useRef, useState} from 'react'
import PropTypes from 'prop-types'
import './missions.css'
import AnimatedText from "./AnimatedCharacters";
import {useInView} from "framer-motion"
import AnimatedCharacters from "./AnimatedCharacters";
import AnimatedWords from "./AnimatedWords";
import {
    motion,
    useScroll,
    useSpring,
    useTransform,
    MotionValue
} from "framer-motion";

function useParallax(value, distance) {
    return useTransform(value, [0, 1], [-distance, distance]);
}

const Missions = (props) => {
    const container = {
        visible: {
            transition: {
                staggerChildren: 0.03
            }
        }
    };
    const ref = useRef(null);
    const { scrollYProgress } = useScroll({ target: ref });
    const y = useParallax(scrollYProgress, 300);

    return (
        <motion.div
            className={`missions-container`}
            initial="hidden"
            variants={container}
            animate={isInView ? "visible" : "hidden"}
        >
            <section>
                <h2 className={`section-index`}>01</h2>
                <div className={`text-container`} ref={ref}>
                    <AnimatedCharacters type={'heading1'} text={'Create'}/>
                    <div>
                        <AnimatedWords type={'heading3'} text={'MULTIPLE CREATIVE TOOLS'}/>
                        <p className={isInView ? "visible" : "hidden"}>From wearables, emotes, scenes, or even names,
                            there
                            is an
                            infinity of things to create and
                            imagine!
                            Explore all the creative tools in decentraland.</p>
                        <button className={`btn_inverted`}>START DEVELOPING</button>
                    </div>
                </div>
                {/*<div className={`img-container`}>*/}
                {/*    <img*/}
                {/*        alt={props.img_alt}*/}
                {/*        src={props.img_src_create}*/}
                {/*        className="create-img"*/}
                {/*    />*/}
                {/*</div>*/}
            </section>

            <section>
                <h2 className={`section-index`}>02</h2>
                <div className={`text-container`}>
                    <h1>Sell</h1>
                    <div>
                        <h3>UNIQUE DECENTRAL MARKET</h3>
                        <p>Is your new outfit grasping a lot of attention? Buy and sell LAND, Estates, Avatar wearables
                            and names in the Decentraland Marketplace: stocking the very best digital goods backed by
                            the ethereum blockchain.</p>
                        <button>GO TO MARKETPLACE</button>
                    </div>
                </div>
                {/*<div className={`img-container`}>*/}
                {/*    <img*/}
                {/*        alt={props.img_alt}*/}
                {/*        src={props.img_src_sell}*/}
                {/*    />*/}
                {/*</div>*/}
            </section>

            <section>
                <h2 className={`section-index`}>03</h2>
                <div className={`text-container`}>
                    <h1>Influence</h1>
                    <div>
                        <h3>UNIQUE DECENTRAL MARKET</h3>
                        <p>Through the DAO, you are in control of the policies created to determine how the world
                            behaves: for example, what kinds of wearable items are allowed (or disallowed) after the
                            launch of the DAO, moderation of content, LAND policy and auctions, among others.</p>
                        <button>Learn more about DAO</button>
                    </div>
                </div>
                {/*<div className={`img-container`}>*/}
                {/*    <img*/}
                {/*        alt={props.img_alt}*/}
                {/*        src={props.img_src_influence}*/}
                {/*    />*/}
                {/*</div>*/}
            </section>
        </motion.div>

    )
}

Missions.defaultProps = {
    img_src_create: '/playground_assets/create.png',
    img_src_sell: '/playground_assets/sell_img.png',
    img_src_influence: '/playground_assets/influence_img.png',
    img_alt: 'image',
    rootClassName: '',
}

Missions.propTypes = {
    img_src: PropTypes.string,
    img_alt: PropTypes.string,
    rootClassName: PropTypes.string,
}

export default Missions
