import React, {useRef, useState} from 'react'
import PropTypes from 'prop-types'
import './missions.css'
import {useInView} from "framer-motion"
import AnimatedCharacters from "./AnimatedCharacters";
import AnimatedWords from "./AnimatedWords";
import {
    motion,
} from "framer-motion";

const letterAnimation = {
    visible: {
        transition: {
            staggerChildren: 0.03
        }
    }
};
const pAnimation = {
    hidden: {
        y: 15,
        opacity: 0,
        transition: {ease: [0.455, 0.03, 0.515, 0.955], duration: 0.85}
    },
    visible: {
        y: 0,
        opacity: 1,
        transition: {staggerChildren: 0.05, ease: [0.455, 0.03, 0.515, 0.955], duration: 0.85},
    }
};

function Section({children}) {
    const ref = useRef(null);
    const isInView = useInView(ref, {amount: 0.5, once: true});

    return (
        <motion.section
            ref={ref}
            initial="hidden"
            variants={letterAnimation}
            animate={isInView ? "visible" : "hidden"}
        >
            {children}
        </motion.section>
    )
}

export default function Missions(props) {
    const createRef = useRef(null);
    const sellRef = useRef(null);
    const influenceRef = useRef(null);

    const createInView = useInView(createRef, {amount: 0.5});
    const sellInView = useInView(sellRef, {amount: 0.5});
    const influenceInView = useInView(influenceRef, {amount: 0.5});


    return (
        <>
            <div className={`missions-container`}>
                <div>
                    <Section>
                        <div className={`section-index desktop`}><AnimatedWords type={'heading2'} text={'01'}
                                                                                y={'50%'}/>
                        </div>
                        <div className={`text-container`} ref={createRef}>
                            <div>
                                <h2 className={`section-index mobile`}>01</h2>
                                <AnimatedCharacters type={'heading1'} text={'Create'}/>
                            </div>
                            <div
                                className={`img-container mobile ${createInView && !sellInView ? "visible" : "hidden"} `}>
                                {/*<img*/}
                                {/*    alt={props.img_alt}*/}
                                {/*    src={props.img_src_create_gradient}*/}
                                {/*/>*/}

                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 -10 100 120" width="213"
                                     className="image">
                                    <title>This is an image title</title>
                                    <desc>This is an image description</desc>
                                    <defs>
                                        <clipPath id="maskImage" clipPathUnits="userSpaceOnUse">
                                            <path d="M100 63A50 50 0 110 70C0 42 20 0 48 0c27 0 52 42 52 70z"/>
                                        </clipPath>
                                        <clipPath id="maskBackground" clipPathUnits="userSpaceOnUse">
                                            <path
                                                d="M190 101a50 50 0 01-50 50 50 50 0 01-50-50 50 50 0 0150-50 50 50 0 0150 50z"/>
                                        </clipPath>
                                    </defs>
                                    <g clipPath="url(#maskImage)" transform="translate(0 -7)">
                                        <image clipPath="url(#maskBackground)" width="120" height="120" x="70" y="38"
                                               href={props.img_src_create_gradient}
                                               transform="translate(-90 -31)"/>
                                        <image width="120" height="144" x="-15" y="0" fill="none"
                                               className="image__foreground"
                                               href={props.img_src_create_character}/>
                                    </g>
                                </svg>

                                <svg width="0" height="0" >
                                    <defs>
                                        <mask id='mask'>
                                    <circle cx="400" cy="400" r="310" fill="#EB25D2"/>
                                        </mask>
                                        {/*<path d="M509 0H250V128.642C154.59 181.494 90 283.204 90 400C90 571.208 228.792 710 400 710C571.208 710 710 571.208 710 400C710 267.151 626.434 153.82 509 109.705V0Z" fill="black"/>*/}
                                    </defs>
                                </svg>

                            </div>
                            <motion.div variants={pAnimation}>
                                <AnimatedWords type={'heading3'} text={'MULTIPLE CREATIVE TOOLS'} y={'200%'}/>
                                <p>
                                    From wearables, emotes, scenes, or even names,
                                    there
                                    is an
                                    infinity of things to create and
                                    imagine!
                                    Explore all the creative tools in decentraland.
                                </p>
                                <button className={`btn_inverted`}>START DEVELOPING</button>
                            </motion.div>
                        </div>
                    </Section>

                    <Section>
                        <div className={`section-index desktop`}><AnimatedWords type={'heading2'} text={'02'}
                                                                                y={'50%'}/>
                        </div>
                        <div className={`text-container`} ref={sellRef}>
                            <div>
                                <h2 className={`section-index mobile`}>02</h2>
                                <AnimatedCharacters type={'heading1'} text={'Sell'}/>
                            </div>
                            <div
                                className={`img-container mobile ${sellInView && !influenceInView ? "visible" : "hidden"} `}>
                                <img
                                    alt={props.img_alt}
                                    src={props.img_src_create_gradient}
                                />
                            </div>
                            <motion.div variants={pAnimation}>
                                <AnimatedWords type={'heading3'} text={'UNIQUE DECENTRAL MARKET'} y={'200%'}/>
                                <motion.p variants={pAnimation}>Is your new outfit grasping a lot of attention? Buy and
                                    sell
