import {Swiper, SwiperSlide} from 'swiper/react';
import {Navigation} from "swiper";

import {useRef} from "react";
import 'swiper/css';
import './swipe.css';

const SwiperContainer = (props) => {
    const swiperRef = useRef();

    return (
        <>
            <Swiper
                spaceBetween={50}
                slidesPerView={'auto'}
                loop={true}
                modules={[Navigation]}
                onBeforeInit={(swiper) => {
                    swiperRef.current = swiper;
                }}
            >
                <SwiperSlide>
                    <div className={'trending-item red'}></div>
                </SwiperSlide>
                <SwiperSlide>
                    <div className={'trending-item blue'}></div>
                </SwiperSlide>
                <SwiperSlide>
                    <div className={'trending-item red'}></div>
                </SwiperSlide>
                <SwiperSlide>
                    <div className={'trending-item blue'}></div>
                </SwiperSlide>
            </Swiper>
            <div>
                <button onClick={() => swiperRef.current?.slidePrev()}>
                    <svg width="48" height="48" viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="24" cy="24" r="23.5" transform="rotate(90 24 24)" stroke="#716B7C"/>
                        <path d="M25.9988 30L27.4088 28.59L22.8288 24L27.4088 19.41L25.9988 18L19.9988 24L25.9988 30Z"
                              fill="white"/>
                    </svg>
                </button>
                <button onClick={() => swiperRef.current?.slideNext()}>
                    <svg width="48" height="48" viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="24" cy="24" r="23.5" transform="rotate(-90 24 24)" stroke="#716B7C"/>
                        <path d="M22.0012 18L20.5912 19.41L25.1712 24L20.5912 28.59L22.0012 30L28.0012 24L22.0012 18Z"
                              fill="white"/>
                    </svg>
                </button>
            </div>
        </>
    )
}


export default SwiperContainer
